async function s({name:r,email:e,password:t}){const a=await fetch("https://v2.api.noroff.dev/auth/register",{method:"POST",headers:{"Content-Type":"application/json"},body:JSON.stringify({name:r,email:e,password:t})});if(!a.ok)throw new Error("Registration failed");return await a.json()}async function i(r){r.preventDefault();const e=document.getElementById("name").value,t=document.getElementById("email").value,n=document.getElementById("password").value;if(console.log({name:e,email:t,password:n}),!e||!t||!n){alert("Please fill in required fields.");return}try{const o=await s({name:e,email:t,password:n});console.log(o),alert("Registration completed! You can now log in."),window.location.href="../login/"}catch(o){console.error("registration error:",o),alert(o.message)}}s();const l=document.forms.register;l.addEventListener("submit",i);
